Templates.template:
createDraftFromActiveDocument(
	"${oContext}", // sap.ui.model.odata.v4.Context
	{
		bPreserveChanges: ${bBPreserveChanges}, // boolean
		fnBeforeCreateDraftFromActiveDocument: ${fnBeforeCreateDraftFromActiveDocument}, // sap.fe.core.actions.draft.ICallback.beforeCreateDraftFromActiveDocument
		fnAfterCreateDraftFromActiveDocument: ${fnAfterCreateDraftFromActiveDocument}, // sap.fe.core.actions.draft.ICallback.afterCreateDraftFromActiveDocument
		fnWhenDecisionToOverwriteDocumentIsRequired: ${fnWhenDecisionToOverwriteDocumentIsRequired} // sap.fe.core.actions.draft.ICallback.whenDecisionToOverwriteDocumentIsRequired
	}
)

navigateToContext(
	"${errorMessage}", // string
	{
		navContainer: ${navContainer}, // sap.m.NavContainer
		description: "${description}", // string
		technicalMessage: "${technicalMessage}", // string
		technicalDetails: "${technicalDetails}" // string
	}
)

createDocument(
	"${OData}", // sap.ui.model.odata.v4.ODataListBinding
	{
		refreshList: ${bRefreshList}, // boolean
		data: ${data}, // map
		busyMode: "${busyMode}", // string
		keepTransientContextOnFailed: ${keepTransientContextOnFailed} // map
	}
)

onCallAction(
	"${sActionName}", // string
	{
		contexts: ${contexts}, // sap.ui.model.odata.v4.Context
		model: ${model}, // sap.ui.model.odata.v4.ODataModel
		invocationGrouping: "${invocationGrouping}", // string
		label: "${label}" // string
	}
)

processBeforeSmartLinkPopoverOpens(
	"${oTableEventParameters}", // object
	"${sSelectionVariant}", // string
	{
		selectionVariant: "${selectionVariant}", // string
		tableVariantId: "${tableVariantId}", // string
		customData: ${customData}, // object
		presentationVariant: ${presentationVariant}, // object
		valueTexts: ${valueTexts} // object
	},
	{
		selectionVariant: ${selectionVariant}, // object
		presentationVariant: ${presentationVariant}, // object
		valueTexts: ${valueTexts} // object
	}
)

storeInnerAppState(
	{
		selectionVariant: "${selectionVariant}", // string
		tableVariantId: "${tableVariantId}", // string
		customData: ${customData}, // object
		presentationVariant: ${presentationVariant}, // object
		valueTexts: ${valueTexts} // object
	},
	"${bImmediateHashReplace}" // boolean
)

storeInnerAppStateWithImmediateReturn(
	{
		selectionVariant: "${selectionVariant}", // string
		tableVariantId: "${tableVariantId}", // string
		customData: ${customData}, // object
		presentationVariant: ${presentationVariant}, // object
		valueTexts: ${valueTexts} // object
	},
	"${bImmediateHashReplace}" // boolean
)
