Templates.template:
new sap.ushell.System(
	{
		alias: "${alias}", // string
		baseUrl: "${baseUrl}", // string
		platform: "${platform}" // string
	}
)

addCatalogTileToGroup(
	"${sCatalogTileId}", // string
	"${sGroupId}", // string
	{
		baseUrl: "${baseUrl}", // string
		remoteId: "${remoteId}" // string
	}
)

setLogonFrameProvider(
	{
		create: ${fn_create}, // function
		destroy: ${fn_destroy}, // function
		show: ${fn_show} // function
	}
)
